import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
    }

    styles = {
        fontSize: "20px"
    }
    
    formatCount () {
        return this.state.count === 0 ? "Zero" : this.state.count
    }

    render() { 
        return (
            <>

                <span style={this.styles} className={"badge badge-primary m-2"} >Zero</span>

                <span>{this.state.count}</span>

                <button oncli style={this.styles} className={"btn btn-secondary btn-sm m-2"} >Increment</button>
            </>
        )

            
    }
}
 
export default Counter;
